'  /**',
'   * @private',
'   * Prints a JSON clob to the browser',
'   *',
'   * @param p_clob: The JSON to be sent to the browser',
'  **/',
'  PROCEDURE print_clob',
'    ( p_clob IN OUT NOCOPY CLOB )',
'  AS',
'    l_size    NUMBER;',
'    l_read    INTEGER := 32000;',
'    l_offset  INTEGER := 1;',
'    l_buffer  VARCHAR2(32767 CHAR);',
'  BEGIN    ',
'    l_size := DBMS_LOB.getlength( lob_loc => p_clob );',
'',
'    LOOP',
'      DBMS_LOB.read(',
'          lob_loc  => p_clob',
'        , amount   => l_read',
'        , offset   => l_offset',
'        , buffer   => l_buffer',
'      );',
'',
'      l_offset := l_offset + l_read;',
'      htp.prn(l_buffer);',
'',
'      EXIT WHEN l_offset >= l_size;',
'    END LOOP;',
'  END print_clob;',
'  /**',
'   * @private',
'   *',
'   * Whenever an error occurs this procedure should be called to structure a response JSON',
'   *',
'   * @param p_error_code : sqlcode should be passed',
'   * @param p_error_message : sqlerrm should be passed',
'   * @param p_backtrace : dbms_utility.format_error_backtrace',
'  **/',
'  procedure print_exception_json',
'    ( p_error_code in varchar2 ',
'    , p_error_message in varchar2',
'    , p_backtrace     in varchar2 ',
'    )',
'  is ',
'    l_json clob;',
'  begin ',
'    apex_json.free_output;',
'    apex_json.initialize_clob_output; ',
'',
'    apex_json.open_object;',
'    apex_json.write(''success'', false );',
'    apex_json.open_object(''err'');',
'    apex_json.write(''code'', p_error_code, true );',
'    apex_json.write(''message'', p_error_message, true );',
'    apex_json.write(''backtrace'', p_backtrace, true );',
'    apex_json.close_all;',
'',
'    l_json := apex_json.get_clob_output;',
'    apex_json.free_output;',
'',
'    print_clob',
'      ( p_clob => l_json );',
'  end print_exception_json;',
'  ',
'  /**',
'   * @private',
'   * Process the query to get the node data',
'   *',
'   * @param p_plsql_statement : PL/SQL statement given by the user in the settings of the plugin',
'  **/',
'  procedure process_nodes_query',
'    ( p_plsql_statement in varchar2 )',
'  is ',
'    l_cursor    PLS_INTEGER;',
'    l_binds_t   dbms_sql.varchar2_table;',
'    l_bind_name varchar2( 30 char );',
'    l_rows      pls_integer;',
'    l_clob      clob;',
'    l_type      varchar2(10 char );',
'  begin',
'    apex_debug.message(''Tree PL/SQL: '' || p_plsql_statement);',
'',
'    -- parse query',
'    l_cursor := dbms_sql.open_cursor;',
'    dbms_sql.parse( l_cursor, ''BEGIN :JSON_RESULT := '' || rtrim(p_plsql_statement, '';'') || ''; END;'', DBMS_SQL.native ); ',
'',
'    -- get bind variables',
'    l_binds_t := WWV_FLOW_UTILITIES.get_binds',
'      ( p_stmt => p_plsql_statement );',
'',
'    if l_binds_t.count > 0 then ',
'      ',
'      for idx in l_binds_t.first..l_binds_t.last loop ',
'        l_bind_name := ltrim( l_binds_t(idx), '':'');',
'        apex_debug.message(''Tree bind name: '' || l_bind_name );',
'',
'        case l_bind_name ',
'          when ''LOAD_TYPE'' then ',
'            dbms_sql.bind_variable( l_cursor, l_bind_name, l_type );',
'            apex_debug.message(''Tree bind value: '' || l_type ); ',
'          when ''PARENT_ID'' then',
'            dbms_sql.bind_variable( l_cursor, l_bind_name, apex_application.g_x02 ); ',
'            apex_debug.message(''Tree bind value: '' || apex_application.g_x02 );',
'          else ',
'            dbms_sql.bind_variable(l_cursor, l_bind_name, v(l_bind_name ));',
'            apex_debug.message(''Tree bind value: '' || v(l_bind_name ));',
'        end case;',
'      end loop;',
'    end if; -- end checks',
'',
'    -- bind OUT parameter',
'    dbms_sql.bind_variable( l_cursor, ''JSON_RESULT'', l_clob );',
'',
'    -- execute query',
'    l_rows := dbms_sql.execute( l_cursor );',
'',
'    -- retrieve JSON',
'    dbms_sql.variable_value( l_cursor, ''JSON_RESULT'', l_clob );',
'    dbms_sql.close_cursor( l_cursor );',
'',
'    -- print JSON',
'    if l_clob is not null then ',
'      print_clob',
'        ( p_clob => l_clob );',
'    end if;',
'  ',
'  exception ',
'    when others then ',
'      dbms_sql.close_cursor( l_cursor );',
'      print_exception_json',
'        ( p_error_code => sqlcode ',
'        , p_error_message => sqlerrm ',
'        , p_backtrace     => dbms_utility.format_error_backtrace',
'        );',
'  end process_nodes_query;',
'  /**',
'   * @private ',
'   *',
'   * Process the query for the search ',
'   *',
'   * @param p_plsql_statement : PL/SQL statement given by the user in the settings by the user',
'  **/',
'  procedure process_search_query',
'    ( p_plsql_statement in varchar2)',
'  is ',
'    l_cursor    pls_integer;',
'    l_binds_t   dbms_sql.varchar2_table;',
'    l_bind_name varchar2( 30 char );',
'    l_rows      pls_integer;',
'    l_clob      clob;',
'    l_type      varchar2(10 char );',
'  begin',
'    apex_debug.message(''Search PL/SQL: '' || p_plsql_statement );',
'',
'    -- parse query',
'    l_cursor := dbms_sql.open_cursor;',
'    dbms_sql.parse(l_cursor, ''BEGIN :JSON_RESULT := '' || rtrim(p_plsql_statement, '';'') || ''; END;'', dbms_sql.native);',
'',
'    -- get bind variables',
'    l_binds_t := wwv_flow_utilities.get_binds ',
'      ( p_stmt => p_plsql_statement );',
'',
'    if l_binds_t.count > 0 then ',
'      for idx in l_binds_t.first..l_binds_t.last loop ',
'        l_bind_name := ltrim( l_binds_t(idx), '':'');',
'        apex_debug.message(''Tree bind name: '' || l_bind_name );',
'',
'        case l_bind_name ',
'          when ''SEARCH_STR'' then ',
'            dbms_sql.bind_variable( l_cursor, l_bind_name, apex_application.g_x02 );',
'            apex_debug.message(''Search bind value: '' || apex_application.g_x02 );',
'          else ',
'            dbms_sql.bind_variable( l_cursor, l_bind_name, v(l_bind_name));',
'            apex_debug.message(''Search bind value: '' || v(l_bind_name));',
'        end case;',
'      end loop;',
'    end if; -- end checks',
'',
'    -- bind OUT parameter',
'    dbms_sql.bind_variable( l_cursor, ''JSON_RESULT'', l_clob );',
'',
'    -- execute query',
'    l_rows := dbms_sql.execute( l_cursor );',
'',
'    -- retreive JSON',
'    dbms_sql.variable_value( l_cursor, ''JSON_RESULT'', l_clob );',
'    apex_debug.message(''return json'');',
'    apex_debug.message(l_clob );',
'    dbms_sql.close_cursor( l_cursor );',
'',
'    -- print JSON',
'    if l_clob is not null then',
'      print_clob',
'        ( p_clob => l_clob );',
'    end if;',
'  ',
'  exception ',
'    when others then ',
'      dbms_sql.close_cursor( l_cursor );',
'      print_exception_json',
'        ( p_error_code => sqlcode ',
'        , p_error_message => sqlerrm ',
'        , p_backtrace     => dbms_utility.format_error_backtrace',
'        );',
'  end process_search_query;',
'----------------------------------------------------------------------------------',
'  FUNCTION render(',
'      p_region              IN APEX_PLUGIN.t_region',
'    , p_plugin              IN APEX_PLUGIN.t_plugin',
'    , p_is_printer_friendly IN BOOLEAN',
'  )',
'  RETURN APEX_PLUGIN.t_region_render_result',
'  AS',
'    l_result       APEX_PLUGIN.t_region_render_result;',
'    l_http_string  VARCHAR2(4000 CHAR);',
'    l_load_type    VARCHAR2(100 CHAR);',
'  BEGIN',
'    l_http_string := ''<div id="dynJSTree" class="t-Form">',
'                        <div class="container">',
'                          <div class="row">',
'                            <div class="col col-12 apex-col-auto">',
'                              <div class="t-Form-fieldContainer t-Form-fieldContainer--floatingLabel apex-item-wrapper--text-field">',
'                                <div class="t-Form-labelContainer">',
'                                  <label for="search_item" id="search_label" class="t-Form-label">Search</label>',
'                                </div>',
'                                <div class="t-Form-inputContainer">',
'                                  <input type="text" name="search" id="search_item" class="text_field apex-item-text"/>',
'                                  <button class="t-Button t-Button--icon t-Button--hot t-Button--noLabel" id="btn_search">',
'                                    <span class="fa fa-search"></span>',
'                                  </button>',
'                                </div>',
'                              </div>',
'                            </div>',
'                          </div>',
'                        </div>',
'                        <div id="tree"></div>',
'                      </div>',
'    '';',
'',
'    HTP.prn(l_http_string);',
'',
'    IF p_region.attribute_02 IS NOT NULL THEN',
'      apex_javascript.add_onload_code ( p_code => ''dynamicTree.init({ajaxIdentifier: "''||apex_plugin.get_ajax_identifier||''",loadType: "DYNAMIC"});'');',
'    ELSIF p_region.attribute_03 IS NOT NULL THEN',
'      apex_javascript.add_onload_code ( p_code => ''dynamicTree.init({ajaxIdentifier: "''||apex_plugin.get_ajax_identifier||''",loadType: "NODE"});'');',
'    ELSE',
'       apex_javascript.add_onload_code ( p_code => ''dynamicTree.init({ajaxIdentifier: "''||apex_plugin.get_ajax_identifier||''",loadType:"BASE"});'');',
'    END IF;',
'',
'    RETURN l_result;',
'  END render;',
'--------------------------------------------------------------------------------',
'',
'--------------------------------------------------------------------------------',
'-- FUNCTION ajax',
'--------------------------------------------------------------------------------',
'  FUNCTION ajax(',
'      p_region IN APEX_PLUGIN.t_region',
'    , p_plugin IN APEX_PLUGIN.t_plugin',
'  )',
'  RETURN APEX_PLUGIN.t_region_ajax_result',
'  AS',
'    l_result             APEX_PLUGIN.t_region_ajax_result;',
'    l_type               VARCHAR2(10 CHAR);',
'    l_plsql_statement    VARCHAR2(32767 CHAR);',
'  BEGIN',
'    -- determine type',
'    IF p_region.attribute_02 IS NOT NULL THEN',
'      l_type := ''DYNAMIC'';',
'    ELSIF p_region.attribute_03 IS NOT NULL THEN',
'      l_type := ''NODE'';',
'    ELSE',
'      l_type := ''BASE'';',
'    END IF;',
'',
'    apex_debug.message(''Tree type: ''|| l_type);',
'',
'    apex_debug.message(''x01: '' || apex_application.g_x01 );',
'    apex_debug.message(''x02: '' || apex_application.g_x02 );',
'    -- check with which config you enter',
'    case apex_application.g_x01 ',
'      when ''GET_NODE'' then ',
'        if apex_application.g_x02 is null then ',
'          l_plsql_statement := p_region.attribute_01;',
'        else ',
'          if p_region.attribute_02 is not null then ',
'            l_plsql_statement := p_region.attribute_02;',
'          elsif p_region.attribute_03 is not null then',
'            l_plsql_statement := p_region.attribute_03;  ',
'          else ',
'            RAISE_APPLICATION_ERROR(-20000, ''Tree PL/SQL statement not found'');',
'          end if;',
'        end if;',
'',
'        process_nodes_query',
'          ( p_plsql_statement => l_plsql_statement );',
'      when ''SEARCH'' then ',
'        if p_region.attribute_04 is not null then ',
'          l_plsql_statement := p_region.attribute_04;',
'          process_search_query',
'            ( p_plsql_statement => l_plsql_statement );',
'        else',
'          raise_application_error(-20000, ''Search PL/SQL statement not found'');',
'        end if;',
'    end case;',
'',
'    RETURN l_result;',
'  END ajax;',
'  --------------------------------------------------------------------------------'))
