'    apex_json.write(''message'', p_error_message, true );',
'    apex_json.write(''backtrace'', p_backtrace, true );',
'    apex_json.close_all;',
'',
'    l_json := apex_json.get_clob_output;',
'    apex_json.free_output;',
'',
